FUNCTION radians(p_degree IN NUMBER)
RETURN NUMBER
IS
    v_rad NUMBER;
BEGIN
    v_rad := p_degree / 180 * pi();

    IF INSTR(TO_CHAR(p_degree),'.') = 0 THEN
